#include <iostream>

// الدالة الرئيسية لإيجاد رقم فيبوناتشي الـ nth
int fibonacci(int n) {
    // الحالة الأساسية
    if (n <= 1) {
        return n;
    }
    // الحالة العامة
    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int n;
    std::cout << "أدخل الرقم nth للعثور على رقم فيبوناتشي: ";
    std::cin >> n;

