        std::cout << "يجب أن يكون الرقم nth غير سالب." << std::endl;
    } else {
        // البدء في حساب رقم فيبوناتشي الـ nth
        int result = fibonacci(n);
        std::cout << "رقم فيبوناتشي الـ " << n << " هو: " << result << std::endl;
    }

    return 0;
}
