#include <iostream>  // هذا يسمح لنا باستخدام الكائن std::cin و std::cout

// هذه الدالة تقوم بحساب الرقم n في سلسلة فيبوناتشي
// نستخدم العودية هنا حيث تعيد الدالة استدعاء نفسها
int fibonacci(int n) {
    if(n <= 1)  // إذا كان n أقل من أو يساوي 1، نعيد n (إما 0 أو 1)
        return n;
    else  // إذا كان n أكبر من 1، نعيد مجموع الرقمين السابقين في السلسلة
        return (fibonacci(n-1) + fibonacci(n-2));
}

int main() {
    int n;  // متغير لتخزين الرقم n المطلوب
    std::cout << "أدخل رقم فيبوناتشي الذي تريد حسابه: ";  // طلب إدخال الرقم من المستخدم
    std::cin >> n;  // قراءة الرقم المدخل من المستخدم

    std::cout << "الرقم " << n << " في سلسلة فيبوناتشي هو " << fibonacci(n) << std::endl;  // طباعة النتيجة

    return 0;
}
```

