#include <iostream>
using namespace std;

int fibonacci(int n) {
    if (n <= 0) {
        return 0;
    } else if (n == 1) {
        return 1;
    } else {
        return fibonacci(n - 1) + fibonacci(n - 2);
    }
}

int main() {
    int n;
    cout << "Enter a positive integer: ";
    cin >> n;

    if (cin.fail()) {
        cout << "Invalid input. Please enter a positive integer." << endl;
        return 0;
    }

    if (n < 0) {
        cout << "Invalid input. Please enter a positive integer." << endl;
    } else {
        cout << "The " << n << "th Fibonacci number is: " << fibonacci(n) << endl;
    }

    return 0;
}

