#include <iostream>
#include <unordered_map>

int fibonacci(int n, std::unordered_map<int, int>& memo, int& count) {
    count++;
    if (n <= 0) {
        std::cout << "Error: n should be a positive integer" << std::endl;
        return 0;
    } else if (n == 1 || n == 2) {
        return 1;
    } else {
        if (memo.find(n) == memo.end()) {
            memo[n] = fibonacci(n-1, memo, count) + fibonacci(n-2, memo, count);
        }
        return memo[n];
    }
}

bool isValidPositiveInteger(int n) {
    return n > 0;
}

int main() {
    int n;
    std::cout << "Enter a positive integer: ";
    std::cin >> n;

    if (!isValidPositiveInteger(n)) {
        std::cout << "Error: n should be a positive integer" << std::endl;
        return 0;
    }

    int count = 0;
    std::unordered_map<int, int> memo;
    int fib = fibonacci(n, memo, count);

    std::cout << "The " << n << "th Fibonacci number is: " << fib << std::endl;
    std::cout << "Total number of recursive calls: " << count << std::endl;

    return 0;
}
