#include <iostream>
#include <vector>
#include <stdexcept>

// Function to find the nth Fibonacci number using recursion with memoization
long long fibonacci(int n, std::vector<long long>& memo) {
    if (n <= 0) {
        throw std::invalid_argument("Input must be a positive integer.");
    }
    if (n == 1) return 0;
    if (n == 2) return 1;

    if (memo[n] != -1) {
        return memo[n];
    }

    memo[n] = fibonacci(n - 1, memo) + fibonacci(n - 2, memo);
    return memo[n];
}

// Function to calculate the sum of all Fibonacci numbers up to the nth number
long long sumFibonacci(int n) {
    if (n <= 0) {
        throw std::invalid_argument("Input must be a positive integer.");
    }

    std::vector<long long> memo(n + 1, -1);
    long long sum = 0;

    for (int i = 1; i <= n; ++i) {
        sum += fibonacci(i, memo);
    }

    return sum;
}

int main() {
    int n;

    std::cout << "Enter a positive integer: ";
    if (!(std::cin >> n)) {
        std::cerr << "Invalid input. Please enter a positive integer." << std::endl;
        return 1;
    }

    try {
        long long nthFib = fibonacci(n, std::vector<long long>(n + 1, -1));
        long long sumFib = sumFibonacci(n);

        std::cout << "The " << n << "th Fibonacci number is: " << nthFib << std::endl;
        std::cout << "The sum of all Fibonacci numbers up to the " << n << "th number is: " << sumFib << std::endl;
