```cpp
#include <iostream>
#include <vector>
#include <stdexcept>
#include <optional>

// Function to compute the nth Fibonacci number using recursion with memoization
long long fibonacci(int n, std::vector<long long>& memo) {
    // Base cases
    if (n == 0) return 0;
    if (n == 1) return 1;

    // Check if the result is already in the memoization table
    if (memo[n] != -1) {
        return memo[n];
    }

    // Compute the result and store it in the memoization table
    memo[n] = fibonacci(n - 1, memo) + fibonacci(n - 2, memo);
    return memo[n];
}

// Wrapper function to handle input validation and memoization initialization
std::optional<long long> fibonacci_wrapper(int n) {
    if (n < 0) {
        throw std::invalid_argument("Input must be a non-negative integer.");
    }

    // Initialize the memoization table with -1 (indicating uncomputed values)
    std::vector<long long> memo(n + 1, -1);

    // Compute the nth Fibonacci number
    return fibonacci(n, memo);
}

int main() {
    try {
        int n;
        std::cout << "Enter a non-negative integer: ";
        std::cin >> n;

        if (std::cin.fail()) {
            throw std::invalid_argument("Invalid input. Please enter an integer.");
        }

        std::optional<long long> result = fibonacci_wrapper(n);
        if (result) {
            std::cout << "The " << n << "th Fibonacci number is: " << *result << std::endl;
        } else {
            std::cout << "Invalid input." << std::endl;
        }
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
    }

    return 0;
}
```

